import "./styles.css";

const current = new Date();
const thisDate = `${current.getMonth()+1}/${current.getDate()}/${current.getFullYear()}`;


const App = () => {

  const [hide, setHide] = useState(false);

  const toggleHide = () => {
    setHide(!hide);
